<script lang="ts">
  import type { WebinarListingsItem } from '../../../types/hubdb';
  import { isUpcoming } from '../../utils/utils';

  let { item }: { item: WebinarListingsItem } = $props();

  const {
    presenter_1_image,
    presenter_1_location,
    presenter_1_name,
    presenter_1_title,
    presenter_2_image,
    presenter_2_location,
    presenter_2_name,
    presenter_2_title,
    presenter_3_image,
    presenter_3_location,
    presenter_3_name,
    presenter_3_title,
    webinar_title,
    cta_label,
    date,
    start_time,
    stop_time,
    language,
    registration_page_url,
  } = item?.values;

  const speaker1 = {
    image_src: presenter_1_image?.url,
    name: presenter_1_name,
    title: presenter_1_title,
    location: presenter_1_location,
  };
  const speaker2 = {
    image_src: presenter_2_image?.url,
    name: presenter_2_name,
    title: presenter_2_title,
    location: presenter_2_location,
  };

  const speaker3 = {
    image_src: presenter_3_image?.url,
    name: presenter_3_name,
    title: presenter_3_title,
    location: presenter_3_location,
  };

  function getDateParts(milliseconds: number) {
    const dateObj = new Date(milliseconds);
    return {
      day: dateObj.getDate(),
      month: dateObj.getMonth() + 1,
      year: dateObj.getFullYear(),
      monthName: dateObj.toLocaleString('default', { month: 'long' }),
      dayName: dateObj.toLocaleString('default', { weekday: 'long' }),
    };
  }

  const dateParts = date ? getDateParts(date) : null;
  const isGerman = language?.name === 'German';
  const isFrench = language?.name === 'French';
  const isEnglish = language?.name === 'English';
</script>

