{#snippet dateTemplate(first: any, second: any)}
  <div class="pt-md absolute left-0 flex h-full w-full flex-col items-center">
    <div class="font-sans-narrow text-center text-4xl font-bold leading-none">
      {first}
    </div>
    <div class="font-sans-narrow text-xl font-medium">
      {second}
    </div>
  </div>
{/snippet}

{#snippet dateBanner()}
  <div class="absolute -top-2 right-4 items-center justify-center text-black">
    <div class="relative h-[9rem] w-[7.5rem]">
      {@render rectangleBg()}

      {#if isGerman}
        {@render dateTemplate(`${dateParts?.day}.`, dateParts?.monthName?.slice(0, 3))}
      {/if}

      {#if isFrench}
        {@render dateTemplate(`${dateParts?.day}`, dateParts?.monthName?.slice(0, 3))}
      {/if}

      {#if isEnglish}
        {@render dateTemplate(`${dateParts?.day}`, dateParts?.monthName?.slice(0, 3))}
      {/if}
    </div>
  </div>
{/snippet}

{#snippet avatar(image_src: string)}
  <div
    class="flex h-12 w-12 items-center justify-center overflow-hidden rounded-full border-2 border-white bg-gray-300 text-xs font-medium text-gray-600"
  >
    <img src={image_src} alt="" class="rounded-full object-contain" />
  </div>
{/snippet}

{#snippet speaker(speaker: any)}
  <div class="flex items-center">
    <div class="mr-3 flex -space-x-6">
      {#if speaker?.image_src}
        {@render avatar(speaker?.image_src)}
      {/if}
    </div>

    <div class="flex-1">
      <div class="text-lg font-semibold text-gray-900">
        {@html speaker?.name}
      </div>
      <div class="text-nickel text-sm font-semibold">
        {speaker?.location}
      </div>
    </div>
  </div>
{/snippet}

<div
  class="border-border mx-auto flex max-w-[32rem] flex-col overflow-hidden rounded-2xl border bg-white"
>
  <div
    class={`bg-prussian-blue relative flex min-h-[18rem]  flex-col ${date && isUpcoming(date) ? 'justify-between' : 'justify-end'} p-md overflow-hidden text-white`}
  >
    {@render shapesBg()}

    {#if date && isUpcoming(date)}
      <div class="relative">
        <span class="mb-4 rounded-3xl bg-[#FFFFFF4F] px-4 py-3 text-sm font-thin text-[#FFFFFF]">
          Upcoming
        </span>
      </div>
    {/if}

    {#if date && isUpcoming(date)}
      {@render dateBanner()}
    {/if}

    <div class="self-end">
      {#if (start_time || stop_time) && isUpcoming(date)}
        <div class=" mt-md relative mb-2">
          <div class="text-lg font-semibold">{start_time} - {stop_time}</div>
        </div>
      {/if}

      <!-- Title -->
      <h5 class="break-word">
        {webinar_title}
      </h5>
    </div>
  </div>

  <!-- Bottom Section with White Background -->
  <div class="p-md my-auto flex flex-col justify-between pb-0">
    <!-- Speakers Section -->
    <div class="gap-sm flex h-full flex-col justify-center">
      {@render speaker(speaker1)}
      {#if speaker2?.name}
        {@render speaker(speaker2)}
      {/if}
      {#if speaker3?.name}
        {@render speaker(speaker3)}
      {/if}
    </div>
  </div>
  <div class="p-md w-full">
    <a
      href={registration_page_url}
      target="_blank"
      class="button bg-imperial-red w-full! block rounded-lg px-4 py-3 text-center font-medium text-white transition-colors duration-200 hover:bg-red-700"
    >
      {cta_label}
    </a>
  </div>
</div>
